import got from 'got'
import mem from 'mem'

export const USER_AGENT = 'ccc-server/1 (https://github.com/frog-pond/ccc-server)'

export const rawGet = (url, opts) =>
	got.get(url, Object.assign({headers: {'User-Agent': USER_AGENT}}, opts))

export const get = (cacheTime) => mem(rawGet, {maxAge: cacheTime})
