import conditional from 'koa-conditional-get'
import etag from 'koa-etag'
import compress from 'koa-compress'
import logger from 'koa-logger'
import responseTime from 'koa-response-time'
import bodyParser from 'koa-bodyparser'
import Router from 'koa-router'
import Koa from 'koa'

async function main() {
	const institution = process.env.INSTITUTION
	if (!institution || institution === 'unknown') {
		console.error(
			'please add -e INSTITUTION=$place to your docker run, or set the environment variable in some way',
		)
		process.exit(1)
	}

	const {v1} = await import(`@frogpond/ccci-${institution}`)

	const app = new Koa()

	//
	// set up the routes
	//
	const router = new Router()
	router.use(v1.routes())

	router.get('/', ctx => {
		ctx.body = 'Hello world!'
	})

	router.get('/ping', ctx => {
		ctx.body = 'pong'
	})

	//
	// attach middleware
	//
	app.use(responseTime())
	app.use(logger())
	app.use(compress())
	// etag works together with conditional-get
	app.use(conditional())
	app.use(etag())
	// parse request bodies
	app.use(bodyParser())
	// hook in the router
	app.use(router.routes())
	app.use(router.allowedMethods())

	//
	// start the app
	//
	const PORT = process.env.NODE_PORT || '3000'
	app.listen(Number.parseInt(PORT, 10))
	console.log(`listening on port ${PORT}`)
}

main()
