import {get, ONE_HOUR} from '@frogpond/ccc-lib'
import mem from 'mem'
import {GH_PAGES} from '../gh-pages'

const GET = mem(get, {maxAge: ONE_HOUR * 6})

let url = GH_PAGES('building-hours.json')

export function getBuildingHours() {
	return GET(url, {json: true}).then(resp => resp.body)
}

export async function buildingHours(ctx) {
	ctx.body = await getBuildingHours()
}
