import {get, ONE_DAY} from '@frogpond/ccc-lib'
import mem from 'mem'

const GET = mem(get, {maxAge: ONE_DAY})

let url = 'https://carls-app.github.io/map-data/'

export function getMap() {
	return GET(url + 'map.json', {json: true}).then(resp => resp.body)
}

export async function map(ctx) {
	ctx.body = await getMap()
}

export function getGeojsonMap() {
	return GET(url + 'map.geojson', {json: true}).then(resp => resp.body)
}

export async function geojson(ctx) {
	ctx.body = await getGeojsonMap()
}
