import {get, ONE_DAY} from '@frogpond/ccc-lib'
import {cafe as getBonAppCafe, menu as getBonAppMenu} from '@frogpond/ccc-lib/bonapp'
import mem from 'mem'

const pauseMenuUrl = 'https://stodevx.github.io/AAO-React-Native/pause-menu.json'
const GET_DAY = mem(get, {maxAge: ONE_DAY})
export const getPauseMenu = () => GET_DAY(pauseMenuUrl, {json: true})

const getMenu = mem(getBonAppMenu, {maxAge: ONE_DAY})
const getInfo = mem(getBonAppCafe, {maxAge: ONE_DAY})

export const getCafe = cafeId =>
	Promise.all([getMenu(cafeId), getInfo(cafeId)])

export async function pauseMenu(ctx) {
	ctx.body = await getPauseMenu().body
}

export async function bonAppMenu(ctx) {
	ctx.body = await getBonAppMenu(ctx.params.cafeId)
}

export async function bonAppCafe(ctx) {
	ctx.body = await getBonAppCafe(ctx.params.cafeId)
}

