import {get, ONE_DAY} from '@frogpond/ccc-lib'
import mem from 'mem'

const GET = mem(get, {maxAge: ONE_DAY})

const base = 'https://carls-app.github.io/carls/'

export function getBus() {
	return GET(base + 'bus-times.json', {json: true}).then(resp => resp.body)
}

export async function bus(ctx) {
	ctx.body = await getBus()
}

export function getModes() {
	return GET(base + 'transportation.json', {json: true}).then(resp => resp.body)
}

export async function modes(ctx) {
	ctx.body = await getModes()
}
