import {get, ONE_DAY} from '@frogpond/ccc-lib'
import mem from 'mem'

const GET = mem(get, {maxAge: ONE_DAY})

let url = 'https://carls-app.github.io/carls/webcams.json'

export function getWebcams() {
	return GET(url, {json: true}).then(resp => resp.body)
}

export async function webcams(ctx) {
	ctx.body = await getWebcams()
}
