import {getMajors} from '../majors'

export const Major = `
	"""
	Major
	"""
	type Major {
		headcount: Int
		name: String
	}
`

export const QueryExtension = `
	extend type Query {
		majors: [Major!]
	}
`

export const typeDefs = [QueryExtension, Major]

export const resolvers = {
	majors: () => getMajors().then(data => data.results),
}
